// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

contract RingsEnhanced {
    struct Ring {
        uint256 id;
        address creator;
        uint256 buyIn;
        uint256 maxPlayers;
        address[] players;
        bool isActive;
        address winner;
        uint256 totalPot;
    }

    uint256 public nextRingId;
    mapping(uint256 => Ring) public rings;
    mapping(uint256 => mapping(address => bool)) public hasJoined;

    event RingCreated(uint256 indexed ringId, address indexed creator, uint256 buyIn, uint256 maxPlayers);
    event PlayerJoined(uint256 indexed ringId, address indexed player);
    event WinnerDeclared(uint256 indexed ringId, address indexed winner, uint256 prize);
    event RingEnded(uint256 indexed ringId, address indexed creator);

    // Create a new ring - creator must pay buy-in
    function createRing(uint256 _maxPlayers) external payable {
        require(_maxPlayers >= 2 && _maxPlayers <= 10, "Players must be between 2-10");
        require(msg.value > 0, "Buy-in required");

        Ring storage newRing = rings[nextRingId];
        newRing.id = nextRingId;
        newRing.creator = msg.sender;
        newRing.buyIn = msg.value;
        newRing.maxPlayers = _maxPlayers;
        newRing.isActive = true;
        newRing.totalPot = msg.value;
        newRing.players.push(msg.sender);

        hasJoined[nextRingId][msg.sender] = true;

        emit RingCreated(nextRingId, msg.sender, msg.value, _maxPlayers);
        emit PlayerJoined(nextRingId, msg.sender);

        nextRingId++;
    }

    // Join an existing ring
    function joinRing(uint256 _ringId) external payable {
        Ring storage ring = rings[_ringId];

        require(ring.isActive, "Ring not active");
        require(msg.value == ring.buyIn, "Incorrect buy-in amount");
        require(ring.players.length < ring.maxPlayers, "Ring full");
        require(!hasJoined[_ringId][msg.sender], "Already joined");

        ring.players.push(msg.sender);
        ring.totalPot += msg.value;
        hasJoined[_ringId][msg.sender] = true;

        emit PlayerJoined(_ringId, msg.sender);
    }

    // End ring and pick winner - only creator can call
    function endRing(uint256 _ringId) external {
        Ring storage ring = rings[_ringId];

        require(ring.isActive, "Ring not active");
        require(msg.sender == ring.creator, "Only creator can end");
        require(ring.players.length >= 2, "Need at least 2 players");

        _pickWinner(_ringId);

        emit RingEnded(_ringId, msg.sender);
    }

    // Internal function to pick winner
    function _pickWinner(uint256 _ringId) internal {
        Ring storage ring = rings[_ringId];

        // Use block properties for randomness (not ideal for production, but ok for testing)
        uint256 randomIndex = uint256(
            keccak256(
                abi.encodePacked(
                    block.timestamp,
                    block.prevrandao,
                    ring.players,
                    _ringId
                )
            )
        ) % ring.players.length;

        address winner = ring.players[randomIndex];
        ring.winner = winner;
        ring.isActive = false;

        // Transfer prize to winner
        uint256 prize = ring.totalPot;
        (bool sent, ) = winner.call{value: prize}("");
        require(sent, "Prize transfer failed");

        emit WinnerDeclared(_ringId, winner, prize);
    }

    // Get ring details
    function getRing(uint256 _ringId) external view returns (
        uint256 id,
        address creator,
        uint256 buyIn,
        uint256 maxPlayers,
        address[] memory players,
        bool isActive,
        address winner,
        uint256 totalPot
    ) {
        Ring storage ring = rings[_ringId];
        return (
            ring.id,
            ring.creator,
            ring.buyIn,
            ring.maxPlayers,
            ring.players,
            ring.isActive,
            ring.winner,
            ring.totalPot
        );
    }

    // Get all players in a ring
    function getRingPlayers(uint256 _ringId) external view returns (address[] memory) {
        return rings[_ringId].players;
    }

    // Check if user has joined a ring
    function userHasJoined(uint256 _ringId, address _user) external view returns (bool) {
        return hasJoined[_ringId][_user];
    }

    // Fallback to accept ETH
    receive() external payable {}
}

